        "    (5, 'Math', 78), (5, 'English', 82), (5, 'History', 77)   # Grades for Eve\n",
        "]\n",
        "\n",
        "cursor.executemany('''\n",
        "INSERT INTO grades (student_id, subject, grade)\n",
        "VALUES (?, ?, ?)\n",
        "''', grades_data)\n",
        "\n",
        "# Commit the changes\n",
        "conn.commit()\n",
        "\n",
        "# Explanation\n",
        "print(\"Students and grades inserted successfully.\")"
