        "    JOIN grades g ON s.student_id = g.student_id\n",
        "''')\n",
        "\n",
        "# Fetch and display the results\n",
        "joined_data = cursor.fetchall()\n",
        "\n",
        "print(\"Joined students' names and grades details:\")\n",
        "for row in joined_data:\n",
        "    print(row)"
